export function AppSidebar() {
  const path = usePathname();

  return (
    <Sidebar>
      <SidebarContent>
        <SidebarGroup>
          <SidebarGroupLabel className="relative flex items-center p-8">
            <Image
              src="/images/jetos.png"
              alt="Jetos Image"
              width="130"
              height="36"
              className="block"
            />
          </SidebarGroupLabel>
          <SidebarGroupContent className="pt-8">
            <SidebarMenu className="gap-6">
              {items.map((item) => (
                <SidebarMenuItem key={item.title}>
                  <SidebarMenuButton className="relative px-8" asChild>
                    <Link
                      href={item.url}
                      className={`peer flex items-center px-4 py-2 text-xs font-semibold ${
                        path === item.url
                          ? "active text-[#1FCB4F]"
                          : "text-gray-400 hover:bg-gray-100 hover:text-gray-800"
                      } `}
                    >
                      <div
                        className={`peer-not-[.active]:text-gray-400 peer-not-[.active]:group-hover:text-gray-800 mr-2 transition-colors duration-300 peer-[.active]:text-white`}
                      >
                        {item.icon}
                      </div>
                      <span
                        className={`peer-not-[.active]:text-gray-400 peer-not-[.active]:group-hover:text-gray-800 transition-colors duration-300 peer-[.active]:text-white`}
                      >
                        {item.title}
                      </span>
                      <div
                        className={`${path === item.url ? "block" : "hidden"} absolute end-0 h-full w-[6px] rounded-bl-[4px] rounded-tl-[4px] bg-[#FFC01E]`}
                      ></div>
                    </Link>
                  </SidebarMenuButton>
                </SidebarMenuItem>
              ))}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>
      </SidebarContent>
    </Sidebar>
  );
}
